import React from 'react'
import axios from "axios";
import { useEffect, useState } from 'react'
import {createContext} from 'react'

export const ArtDataContext = createContext({
    artData: {},
    setArtData: () => null,
  }); 

function DataContext() {


    const ArtData = (url) => {
        const [artData, setArtData] = useState(null);
        const [error, setError] = useState("");
        const [loaded, setLoaded] = useState(false);

        useEffect(() => {
          axios
            .get(url)
            .then((response) => setArtData(response.data))
            .catch((error) => setError(error.message))
            .finally(() => setLoaded(true));
        }, []);

        return { artData, error, loaded };
      };

      const {artData,error,loaded} = ArtData('http://localhost:3001/v1/heartofvalley/features')

  return (
    <ArtDataContext.Provider value={{artData, setArtData}}>
    {children}
    </ArtDataContext.Provider>
  )
}

export default DataContext
