import {useState} from 'react'
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import "./ContactUs.css"  


function ContactUs() {
/*     const [fullName, SetfullName] = useState('')
    const [email, SetEmail] = useState('')
    const [message, SetMessage] = useState('')  */

    const formik = useFormik({
        initialValues: {
          fullName: '',
          email: '',
          message:'',
        },
        validationSchema: Yup.object({
          fullName: Yup.string()
            .max(30, 'Must be 15 characters or less')
            .required('Required'),
          email: Yup.string().email('Invalid email address').required('Required'),
          message: Yup.string()
            .max(6000, 'Must be 30 characters or less')
            .required('Required'),
        }),
        onSubmit: values => {
          alert(JSON.stringify(values, null, 2));
        },
      });

  return (
      <div className="container pt-4 d-flex main" >
          <div className=' headerTitle'>
              <h1>We'd love to hear from you</h1>
          </div>
        <Form className='container-fluid' noValidate onSubmit={handleSubmit}>

          <Row className="mt-2">
            <Form.Group as={Col} md="12" controlId="validationFormik03">
              <Form.Label className='labelAlign' >Full Name</Form.Label>
              <Form.Control
                type="text"
                name="Full Name"
                className='rounded-pill bg-light p-4'
                value={fullName}
                onChange={formik.handleChange}
                isInvalid={!!errors.fullName}
                placeholder="Enter your full name"
              />

    {formik.touched.fullName && formik.errors.fullName ? (
         <div>{formik.errors.fullName}</div>
       ) : null}    
            </Form.Group>
          </Row>
          <Row className="mt-2">
            <Form.Group as={Col} md="12" controlId="validationFormik03">
              <Form.Label className='labelAlign' >Email</Form.Label>
              <Form.Control
                type="email"
                name="Email"
                className='rounded-pill bg-light p-4'
                placeholder="Enter your valid email"
                value={email}
                onChange={(e)=>{SetEmail(e.target.value)}}
                isInvalid={!!errors.email}
              />

              <Form.Control.Feedback type="invalid" className='feedbackAlign'>
                Invalid email
              </Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Row className="mt-2">
            <Form.Group as={Col} md="12" controlId="validationFormik03">
              <Form.Label className='labelAlign'  >Message</Form.Label>
              <Form.Control
                as="textarea"
                rows={5}
                className='bg-light pt-3 pl-4'
                style={{borderRadius: "20px"}}
                name="Message"
                placeholder="Enter your valid email address"
                value={message}
                onChange={(e)=>{SetMessage(e.target.value)}}
                isInvalid={!!errors.message}
              />

              <Form.Control.Feedback type="invalid" className='feedbackAlign'>
                Please type your message
              </Form.Control.Feedback>
            </Form.Group>

          </Row>
          <Form.Group className="mb-4 labelAlign">
            <Form.Check
              required
              name="terms"
              label="I want to receive a copy of this message to my email"
              onChange={handleCheckBox}
              isInvalid={!!errors.terms}
              feedback={errors.terms}
              feedbackType="invalid"
              id="validationFormik0"

            />
          </Form.Group >
          <div className="container-fluid mt-4 text-center">
          <Button className="fs-5  px-5 rounded-pill" style={{backgroundColor:'#0F4C64'}}type="submit">Submit</Button>
          </div>

        {/* </Form.group> */}
        </Form>

    </div>

  ); 
}

export default ContactUs;
