import React from 'react'

import FeatureCard from '../components/FeatureCard'
import { useState, useEffect } from 'react'
import axios from "axios";


function SearchGrid() {

   const ArtData = (url) => {
    const [data, setData] = useState(null);
    const [error, setError] = useState("");
    const [loaded, setLoaded] = useState(false);

    useEffect(() => {
      axios
        .get(url)
        .then((response) => setData(response.data))
        .catch((error) => setError(error.message))
        .finally(() => setLoaded(true));
    }, []);

    return { data, error, loaded };
  };

  const {data,error,loaded} = ArtData('http://localhost:3001/v1/heartofvalley/features')

  return (
    <div className="container mt-5 d-flex justify-content-around flex-wrap ">
       {(loaded === true) ? artData.map((data, key) => {
                return <FeatureCard imgAddr={data.imagePath}  title={data.title} />;
            }): <h4>Loading Error...</h4>} 
       {/*  {artData.map((data, key) => {
                return <FeatureCard imgAddr={data.imagePath}  title={data.title} />;
            })}  */}
    </div>
  )
}

export default SearchGrid
