import {useState} from 'react'
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';
import { Formik } from 'formik';
import { string, object } from 'yup';
//const { Formik } = formik;
//import './Contact.css'

const schema = object().shape({
  fullName: string().required(),
  email: string().required(),
  message: string().required(),
  /* terms: yup.bool().required().oneOf([true], 'Terms must be accepted'), */
});

function ContactUs() {
    const [fullName, SetfullName] = useState('')
    const [email, SetEmail] = useState('')
    const [message, SetMessage] = useState('') 

    const handleFullName = (e) => {
        e.preventDefault()
        console.log(e)
    }
  return (
      <div className="container d-flex main" style={{}}>
          <div className=''>
              <h1>We'd love to hear from you</h1>
          </div>
    <Formik

      validationSchema={schema}
      onSubmit={console.log}
      initialValues={{
        fullName: fullName,
        email: email,
        message: message,
        /* terms: false, */
      }}
    >
      {({
        handleSubmit,
        handleMessage,
        handleFullName,
        handleEmail,
        handleCheckBox,
        handleChange,
        handleBlur,
        values,
        touched,
        isValid,
        errors,
      }) => (
        <Form className='container-fluid' noValidate onSubmit={handleSubmit}>

          <Row className="">
            <Form.Group as={Col} md="10" controlId="validationFormik03">
              <Form.Label className='labelAlign'>Full Name</Form.Label>
              <Form.Control
                type="text"
                name="Full Name"
                className='rounded-pill bg-light'
                value={fullName}
                onChange={(e)=>{SetfullName(e.target.value)}}
                isInvalid={!!errors.fullName}
                placeholder="Enter your full name"
              />

              <Form.Control.Feedback type="invalid" className='feedbackAlign'>
                Enter your full name
              </Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Row className="">
            <Form.Group as={Col} md="10" controlId="validationFormik03">
              <Form.Label className='labelAlign'>Email</Form.Label>
              <Form.Control
                type="email"
                name="Email"
                className='rounded-pill bg-light'
                placeholder="Enter your valid email"
                value={email}
                onChange={(e)=>{SetEmail(e.target.value)}}
                isInvalid={!!errors.email}
              />

              <Form.Control.Feedback type="invalid" className='feedbackAlign'>
                Invalid email
              </Form.Control.Feedback>
            </Form.Group>
          </Row>
          <Row className="">
            <Form.Group as={Col} md="10" controlId="validationFormik03">
              <Form.Label className='labelAlign'>Message</Form.Label>
              <Form.Control
                as="textarea"
                rows={4}
                className='bg-light'
                style={{borderRadius: "20px"}}
                name="Message"
                placeholder="Enter your valid email address"
                value={message}
                onChange={(e)=>{SetMessage(e.target.value)}}
                isInvalid={!!errors.message}
              />

              <Form.Control.Feedback type="invalid" className='feedbackAlign'>
                Please type your message
              </Form.Control.Feedback>
            </Form.Group>

          </Row>
          <Form.Group className="mb-3">
            <Form.Check
              required
              name="terms"
              label="I want to receive a copy of this message to my email"
              onChange={handleCheckBox}
              isInvalid={!!errors.terms}
              feedback={errors.terms}
              feedbackType="invalid"
              id="validationFormik0"

            />
          </Form.Group >
          {/* <Form.group className="container-fluid"> */}
          <div className="container-fluid text-center">
          <Button className="fs-6  px-5 rounded-pill" style={{backgroundColor:'#0F4C64'}}type="submit">Submit</Button>
          </div>

        {/* </Form.group> */}
        </Form>
      )}
    </Formik>
    </div>

  ); 
}

export default ContactUs;
