import React from 'react'
import '../assets/stylesheets/featureCard.css'
import ArtDetails from './ArtDetails'
import { useHistory } from "react-router-dom";

function FeatureCard({imgAddr,title}) {
   const history = useHistory()

  return (

    <div className="featureCard" style={{
      backgroundImage: `linear-gradient(to bottom,  rgba(0,0,0,0.6) ,rgba(255,255,255,0)),url(${imgAddr})`,
      backgroundSize:"cover",
      backgroundRepeat: "no-repeat", 
      marginBottom: "40px", 
      borderRadius:'25px'}} onClick={()=>{history.push({
        pathname: '/artDetails',
        state: {  // location state
          imgAddr: imgAddr, 
        },
      })}}>
      <h5 className="title">{title}</h5>
    </div>

  )
}

export default FeatureCard
